                $designation_name = $designation[0]->designation_name;
            } else {
                $designation_name = '--';
            }
            // department
            $department = $this->Department_model->read_department_information($r->department_id);
            if (!is_null($department)) {
                $department_name = $department[0]->department_name;
            } else {
                $department_name = '--';
