        // ===============================================================================================================
        $employee           = $this->Employees_model->get_rekap_kehadiran_borongan_reguler($company_id, $month_year);

        $data = array();

        $no = 1;

        foreach ($employee->result() as $r) {

            if ($r->bulan == 1 || $r->bulan == 2) { // 31

                $info_T29 = $r->T29;
                $info_T30 = $r->T30;
                $info_T31 = $r->T31;
            } else 	if ($r->bulan == 3) { // 28

                $info_T29 = '';
                $info_T30 = '';
                $info_T31 = '';
            } else 	if ($r->bulan == 4) { // 31

                $info_T29 = $r->T29;
                $info_T30 = $r->T30;
                $info_T31 = $r->T31;
            } else 	if ($r->bulan == 5) {

                $info_T29 = '';
                $info_T30 = '';
                $info_T31 = '';
            } else 	if ($r->bulan == 6) {

                $info_T29 = $r->T29;
                $info_T30 = $r->T30;
                $info_T31 = $r->T31;
            } else 	if ($r->bulan == 7) {

                $info_T29 = $r->T29;
                $info_T30 = $r->T30;
                $info_T31 = '';
            } else {

                $info_T29 = '';
                $info_T30 = '';
                $info_T31 = '';
            }

            $data[] = array(
                $no,
                strtoupper($r->full_name),

                $r->T21,
                $r->T22,
                $r->T23,
                $r->T24,
                $r->T25,
                $r->T26,

                $r->T27,
                $r->T28,


                $info_T29,
                $info_T30,
                $info_T31,

                $r->T01,
                $r->T02,
                $r->T03,
                $r->T04,
                $r->T05,
                $r->T06,
                $r->T07,
                $r->T08,
                $r->T09,
                $r->T10,

                $r->T11,
                $r->T12,
                $r->T13,
                $r->T14,
                $r->T15,
                $r->T16,
                $r->T17,
                $r->T18,
                $r->T19,
                $r->T20,

                $r->libur,
                $r->libur_kantor,
                $r->aktif,
                $r->sakit,
                $r->izin,
                $r->cuti,
                $r->alpa,
                $r->dinas,
                $r->terlambat_menit,
                $r->terlambat_jam,
                $r->total
            );
            $no++;
        }

        $output = array(
            "draw"            => $draw,
            "recordsTotal"    => $employee->num_rows(),
            "recordsFiltered" => $employee->num_rows(),
            "data" => $data
        );

        echo json_encode($output);
        exit();
    }

